```jsx
import React, { useState, useEffect } from 'react';
import axios from 'axios';

function CollectionList() {
  const [collections, setCollections] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:9000/collections')
      .then(response => {
        setCollections(response.data);
      })
      .catch(error => {
        console.error(error);
      });
